<?phpfunction __autoload($classname){	switch ($classname[0])	{		case 'C':			include_once("c/Classes/$classname.php");			break;		case 'M':			include_once("c/Model/$classname.php");	}}$action = 'action_';$action .= (isset($_GET['act'])) ? $_GET['act'] : 'index';switch ($_GET['c']){	case 'articles':		$controller = new C_Articles();		break;	case 'editor':		$controller = new C_Editor();		break;	default:		$controller = new C_Articles();}$controller->Request($action);